%spark
import org.apache.spark.sql.functions._ 

//Useful function for checking outputs for dataframe/dataset
/*
 df.first()
 df.show()
 str_edges.first()
*/

// ======================= Read in data ============================

//Read in local file (textfile)?
val graphData = sc.textFile("/Users/Shan/projects/bigdata-viz/data/data.txt")

//Create a case class???
case class Edge(source: Integer, target: Integer)

//Map the intput, convert to integer
var data = graphData.map(s=>s.split(" "))
                    .map(s=>Edge(s(0).toInt, s(1).toInt))



// ====================== Process nodes =============================

//Conver RDD to data frame
var data_df = data.toDF()

//Add another column, with index
var df = data_df.withColumn("id",monotonicallyIncreasingId + 1)

//Get distinct src nodes
var src = df.select("source").distinct()
//Get distinct tgt nodes
var tgt = df.select("target").distinct()

//Get the union of both src and tgt nodes, then select only distinct
var distinct_nodes = src.union(tgt).distinct()
//distinct_nodes.show()


//1. Map the nodes into string
/*
Before     After

Node       Node
1         {"id": 1}
2         {"id": 2}
3         {"id": 3}
...
*/

//2. collect() convert the column into an array: [{"id": 1}, {"id": 2},  {"id": 3}, ...]
//3. mkString(",") concatenate the array into a string: "{"id": 1}, {"id": 2},  {"id": 3}, ..."
var str_nodes = distinct_nodes.map(row => s"""{"id": ${row.get(0)}}""").collect().mkString(",")

//Add [] to the beginning and end of the string
var nodes = s"""[$str_nodes]"""


// ====================== Process edges =============================

//Map the dataframe into edges
var str_edges = df.map(e=>s"""{"source": ${e(0)}, "target": ${e(1)}, "id": ${e(2)}}""").collect().mkString(",")

var edges = s"""[$str_edges]"""

// ====================== Use nodes and edges for %network ==================

print(s"""
%network {
    "nodes": $nodes,
    "edges": $edges
}
""")
